import React, { useEffect, useRef, useState } from 'react';

const FaceLandmarkComponent = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [faceLandmarker, setFaceLandmarker] = useState(null);

    useEffect(() => {
        const loadModel = async () => {
            if (!window.vision) {
                console.error("MediaPipe Tasks Vision library not loaded");
                return;
            }

            const filesetResolver = await window.vision.FilesetResolver.forVisionTasks(
                'path/to/wasm/root' // Adjust the path to your WASM files
            );

            const loadedFaceLandmarker = await window.vision.FaceLandmarker.createFromOptions(
                filesetResolver, {
                    baseOptions: {
                        modelAssetPath: 'path/to/face_landmarker.task', // Adjust the path to your model
                        delegate: 'GPU',
                    },
                    runningMode: 'VIDEO',
                    numFaces: 1,
                    // Additional configuration options if needed
                }
            );

            setFaceLandmarker(loadedFaceLandmarker);
        };

        loadModel();
    }, []);


    useEffect(() => {
        const startVideo = async () => {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            videoRef.current.srcObject = stream;
        };

        startVideo();
    }, []);

    useEffect(() => {
        if (!faceLandmarker || !videoRef.current) return;

        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');

        const processFrame = async () => {
            if (videoRef.current.readyState < 2) {
                requestAnimationFrame(processFrame);
                return;
            }

            canvas.width = videoRef.current.videoWidth;
            canvas.height = videoRef.current.videoHeight;

            const results = await faceLandmarker.detect(videoRef.current);
            if (results && results.multiFaceLandmarks) {
                // Draw landmarks here
                // Example: Draw a circle for each landmark
                context.clearRect(0, 0, canvas.width, canvas.height);
                results.multiFaceLandmarks.forEach(landmarks => {
                    landmarks.forEach(landmark => {
                        context.beginPath();
                        context.arc(landmark.x * canvas.width, landmark.y * canvas.height, 3, 0, 2 * Math.PI);
                        context.fillStyle = 'red';
                        context.fill();
                    });
                });
            }

            requestAnimationFrame(processFrame);
        };

        requestAnimationFrame(processFrame);
    }, [faceLandmarker]);

    return (
        <div>
            <video ref={videoRef} autoPlay playsInline style={{ display: 'none' }} />
            <canvas ref={canvasRef} />
        </div>
    );
};

export default FaceLandmarkComponent;
